INSERT INTO `wced_entry` VALUES (17214, "suga1", "918", "<entry page=""918"" id=""17214""><form id=""sugaq"" lang=""ceb"">sugà<sub>1</sub></form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>lamp</tr>. </trans>
