INSERT INTO `wced_entry` VALUES (17231, "sugid", "920", "<entry page=""920"" id=""17231""><form id=""suxgid"" lang=""ceb"">súgid</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[AN2; c6]</itype><trans> <tr>tell</tr>, <tr>relate</tr>. </trans>
<eg><i lang=""ceb"">Kinahanglan nga musúgid ka sa tinúud nga nahitabù,</i><trans> You must relate what really happened. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AN; c]</itype><trans> <tr>confess</tr> one’s sin to a priest to obtain absolution. </trans>
<eg><i lang=""ceb"">Human siya makasúgid (makapanúgid) gimaymáyan siya sa párì,</i><trans> After he confessed his sins, the priest gave him words of advice. </trans></eg>
</sense>
<sense n=""3""><number>3</number> <itype>[c3]</itype><trans> <tr>be heard of</tr>. </trans>
<eg><i lang=""ceb"">Wà na igsúgid ang nangalúnud sa barku,</i><trans> The passengers of the ship that sank were never heard of again. </trans></eg>
<eg><i lang=""ceb"">Ikasúgid ka pa kahag nakauban ka pa sa Byitnam?</i><trans> Do you think you would still be alive if you had been sent to Vietnam? </trans></eg>
</sense></hom>
<entry><form id=""panuxgid"" lang=""ceb""><abbr expan=""panúgid"" type=""lemma"">paN-</abbr></form>
<hom>
<sense n=""0""> <itype>[A2]</itype><trans> = <xr lang=""ceb"" target=""#suxgid""><sc>súgid</sc></xr>, <i lang=""ceb"">v1.</i> </trans>
</sense></hom></entry>
<entry><form id=""walay_panuxgid"" lang=""ceb"">walay <abbr expan=""panúgid"" type=""lemma"">paN-</abbr></form>
<hom>
<sense n=""0""><trans> <tr>wiped out</tr>, <tr>annihilated</tr> (lit. nobody was left to tell the story). </trans>
<eg><i lang=""ceb"">Way nanúgid sa inasal nga gidá sa piknik,</i><trans> The roast pig that was brought to the picnic was wiped out. </trans></eg>
</sense></hom></entry>
<entry><form id=""hinuxgid"" lang=""ceb""><abbr expan=""hinúgid"" type=""lemma"">hiN-</abbr></form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A12S; b(1)]</itype><trans> tell stories at length. </trans>
<eg><i lang=""ceb"">Naghinugid siya sa mga primyu nga íyang nadaug,</i><trans> He related in great length the prizes he won. </trans></eg>
</sense></hom></entry>
<entry><form id=""sugilaxnun"" lang=""ceb"">sugilánun</form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>short story</tr>. </trans>
</sense></hom></entry>
<entry><form id=""magsusugilanun"" lang=""ceb"">magsusugilanun</form>
<hom>
<sense n=""0""><trans> short story writer. </trans>
</sense></hom></entry>
<entry><form id=""igsusuxgid"" lang=""ceb""><abbr expan=""igsusúgid"" type=""lemma"">ig-r-</abbr></form>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>confession</tr>. </trans>
</sense></hom></entry>
<entry><form id=""kasugixran"" lang=""ceb"">kasugíran</form>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> <tr>history</tr>, a related series of past events. </trans>
</sense>
<sense n=""2""><number>2</number><trans> <tr>legend</tr>, s.t. handed down by tradition. <ix>see also</ix> <xr lang=""ceb"" target=""#sugixlun""><sc>sugílun</sc></xr>.†
</trans>
</sense></hom></entry></entry>");
