INSERT INTO `wced_entry` VALUES (17259, "sugud", "921", "<entry page=""921"" id=""17259""><form id=""suxgud"" lang=""ceb"">súgud</form>
<hom role=""v""><pos>v</pos>
<sense n=""0""> <itype>[A; b5]</itype><trans> <tr>start</tr>, <tr>begin</tr> s.t.; begin doing s.t. </trans>
<eg><i lang=""ceb"">Nagsúgud ug kapakpak ang pintal,</i><trans> The paint is starting to flake off. </trans></eg>
<eg><i lang=""ceb"">Sugdun (sugdan) nátù ug limpiyu dinhi,</i><trans> Let us start cleaning here. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""0""><trans> <tr>start</tr>, <tr>beginning</tr>. </trans>
<eg><i lang=""ceb"">Ang hagwà <pb n=""922""/>mauy súgud (sinugdánan) sa áway,</i><trans> Horseplay is the beginning of a quarrel. </trans></eg>
<eg><i lang=""ceb"">Súgud ugmà wà nay byáhi sa Buhul,</i><trans> Starting tomorrow there will be no more trips to Bohol. </trans></eg>
</sense></hom>
<entry><form id=""sugudsuxgud"" lang=""ceb"">sugudsúgud</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A13; b6]</itype><trans> start s.t. with no intention of finishing it. </trans>
<eg><i lang=""ceb"">Nagsugudsúgud ka man ug túkud ug balay nga walà ka may kwartang ikagastu,</i><trans> You are starting to construct a house when you don’t have money to finish it. </trans></eg>
</sense>
