INSERT INTO `wced_entry` VALUES (17260, "suguk", "922", "<entry page=""922"" id=""17260""><form id=""suxguk"" lang=""ceb"">súguk</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; c1P]</itype><trans> for liquid to rush and gather s.w. </trans>
