INSERT INTO `wced_entry` VALUES (17278, "suhut", "923", "<entry page=""923"" id=""17278""><form id=""suxhut"" lang=""ceb"">súhut</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a12]</itype><trans> go into or pass through a place, esp. with tall trees or grasses. </trans>
<eg><i lang=""ceb"">Pagbantay sa limátuk ug musúhut mu sa lasang,</i><trans> Watch out for leeches when you go through the forest. </trans></eg>
