INSERT INTO `wced_entry` VALUES (17522, "supak", "939", "<entry page=""939"" id=""17522""><form id=""suxpak"" lang=""ceb"">súpak</form>
<hom role=""v""><pos>v</pos>
<sense n=""1""><number>1</number> <itype>[A; a12]</itype><trans> <tr>go against</tr>, violate. </trans>
<eg><i lang=""ceb"">Dílì ku musúpak sa súgù sa maistru,</i><trans> I will not disobey the teacher’s orders. </trans></eg>
</sense>
<sense n=""1a""><number>1a</number> <itype>[B1256; a1c]</itype><trans> cut against the grain in wood working. </trans>
<eg><i lang=""ceb"">Ayaw ug isúpak ang tumung sa tigib arun dílì gáhì nga ihandus,</i><trans> Don’t chisel against the grain or it will be hard to push. </trans></eg>
</sense>
<sense n=""2""><number>2</number> <itype>[AC2; a12]</itype><trans> <tr>oppose</tr>, be contrary to each other. </trans>
<eg><i lang=""ceb"">Musúpak ku sa ímung plánu,</i><trans> I will oppose your plans. </trans></eg>
<eg><i lang=""ceb"">Nagsúpak ang ámung mga paminsar,</i><trans> Our opinions are contrary to each other. </trans></eg>
</sense></hom>
<hom role=""n""><pos>n</pos>
<sense n=""1""><number>1</number><trans> person opposed. </trans>
<eg><i lang=""ceb"">Kadtung súpak muísa sa ílang kamut,</i><trans> Those who are opposed, raise their hands. </trans></eg>
</sense>
<sense n=""2""><number>2</number><trans> s.t. contrary, against. </trans>
<eg><i lang=""ceb"">Supak sa ákung kabubut-un nga mulakaw <pb n=""940""/>ka sa walay pananghid,</i><trans> It is against my will if you go without asking my permission first. </trans></eg>
